        this.baseUrl = environment.apiUrl;
        this.hubUrl = environment.hubUrl;
        this.messageThreadSource = new BehaviorSubject([]);
        this.messageThread$ = this.messageThreadSource.asObservable();
    }
    createHubConnection(user, otherUsername) {
        this.hubConnection = new HubConnectionBuilder()
            .withUrl(`${this.hubUrl}message?user=${otherUsername}`, { accessTokenFactory: () => user.token })
            .withAutomaticReconnect()
            .build();
        this.hubConnection.start().catch(err => console.log(err));
        this.hubConnection.on('ReceiveMessageThread', messages => {
            this.messageThreadSource.next(messages);
        });
        this.hubConnection.on('SendMessage', message => {
            this.messageThread$.pipe(take(1)).subscribe(messages => {
                this.messageThreadSource.next([...messages, message]);
            });
        });
        this.hubConnection.on('UpdatedGroup', (group) => {
            if (group.connections.some(x => x.username === otherUsername)) {
                this.messageThread$.pipe(take(1)).subscribe(messages => {
                    messages.forEach(message => {
                        if (!message.dateRead) {
                            message.dateRead = new Date(Date.now());
                        }
                    });
                    this.messageThreadSource.next([...messages]);
                });
            }
        });
    }
    stopHubConnection() {
        if (this.hubConnection) {
            this.hubConnection.stop();
        }
    }
    getMessages(pageNumber, pageSize, container) {
        let params = getPaginationParams(pageNumber, pageSize);
        params = params.append('container', container);
        return getPaginatedResult(`${this.baseUrl}messages`, params, this.http);
    }
    getMessageThread(username) {
        return this.http.get(`${this.baseUrl}messages/thread/${username}`);
    }
    async sendMessage(username, content) {
        const createMessgae = { recipientUsername: username, content: content };
        return this.hubConnection.invoke('NewMessage', createMessgae)
            .catch(err => console.log(err));
    }
    deleteMessage(id) {
        return this.http.delete(`${this.baseUrl}messages/${id}`);
    }
}
